const podcrackSearch=document.getElementById("podcrack-search"),podcrackSubmit=document.getElementById("podcrack-submit"),podcastContainer=document.getElementById("podcast-container");podcrackSubmit.addEventListener("click",(function(){podcastContainer.innerHTML="";let e=homeboxUrl+"/apps/podcrack/podcast/search?search-query="+podcrackSearch.value.replace(" ","+");fetch(e).then((e=>e.json())).then((e=>{for(let c of e.feeds)podcastContainer.innerHTML+='<div class="results-podcast"><div class="results-podcast-details"><img class="results-podcast-artwork" src="'+c.artwork+'"><h3 class="results-podcast-title">'+c.title+'</h3><p class="results-podcast-author">'+c.author+'</p></div><div class="results-podcast-view-episodes"><a href="/apps/podcrack/podcast/view?object='+encodeURIComponent(JSON.stringify(c))+'">View Podcast</a><i class="fa-regular fa-chevron-right"></i></div></div>'})).catch((e=>{console.error("Error:",e)}))}));